		auto padding = ImGui::GetStyle().WindowPadding;
		float width1 = 0.0f;
		float height1 = 0.0f;
		float width2 = 0.0f;
		float height2 = 0.0f;
		width2 = std::max(width2, knobWidth);
		height2 += knobHeight;
		width2 = std::max(width2, knobWidth);
		height2 += knobHeight;
		width2 += padding.x * 2.0f;
		height2 += frame + padding.y * 2.0f;
		width1 += width2;
		height1 = std::max(height1, height2);
		width1 += spacing.x;
		float width3 = 0.0f;
		float height3 = 0.0f;
		width3 = std::max(width3, knobWidth);
		height3 += knobHeight;
		width3 = std::max(width3, knobWidth);
		height3 += knobHeight;
		width3 = std::max(width3, knobWidth);
		height3 += knobHeight;
		width3 += padding.x * 2.0f;
		height3 += frame + padding.y * 2.0f;
		width1 += width3;
		height1 = std::max(height1, height3);
		width1 += spacing.x;
		float width4 = 0.0f;
		float height4 = 0.0f;
		width4 = std::max(width4, knobWidth);
		height4 += knobHeight;
		width4 = std::max(width4, knobWidth);
