		width4 = std::max(width4, knobWidth);
		height4 += knobHeight;
		width4 += padding.x * 2.0f;
		height4 += frame + padding.y * 2.0f;
		width1 += width4;
		height1 = std::max(height1, height4);
		width1 += spacing.x;
		float width5 = 0.0f;
		float height5 = 0.0f;
		width5 = std::max(width5, knobWidth);
		height5 += knobHeight;
		width5 = std::max(width5, knobWidth);
