		float lowLevel = 0.0f;
		float lowFreq = 100.0f;
		float peak1Level = 0.0f;
		float peak1Freq = 100.0f;
		float peak1Q = 1.0f;
		float peak2Level = 0.0f;
		float peak2Freq = 100.0f;
